                  </label>{" "}
                  <input
                    type="radio"
                    class="btn-check listTypeButton"
                    name="options"
                    value="1"
                    id="option2"
                    autocomplete="off"
                  ></input>
                  <label class="btn btn-secondary" for="option2">
                    Todo
                  </label>
                </div>
              </div>
              <button
                type="button"
                disabled={!addState.addTitle.replace(/\s/g, "")}
                class="btn addItemButton"
