import React, { setState, useState, useEffect } from "react";
import "./Popup.js";
import Popup from "./Popup.js";
import axios from "axios";
import './RegistrationForm/RegistrationForm.css'
function Friends() {

  function FriendContainer(props){
    return(
    props.state.localFriends.map((item, index) => {
      return(<button class="btn friendBox">{'@' + item.login}</button>)
    })
    )
  }

  const jwt = require("jsonwebtoken");
  var storage = require("../tokenStorage.js");

  var tok = storage.retrieveToken();
  var ud = jwt.decode(tok, { json: true });

  var localUserID = ud.userID;

  const [buttonPopup, setButtonPopup] = useState({
    popupState: false
  });
  const [friendReq, setFriend] = useState([]);
  const [state, setState] = useState({
    localFriends: []
  });

  //function to get the friends data
  const getFriends = () => {
    //get is getting every response by the endpoint
    const payload = { userID: localUserID };
    axios
      .post("https://letsbuckit.herokuapp.com/api/fr-allfriends", payload)
      .then((response) => {
        setState({
          localFriends:response.data.results
        });
      })
      .catch(err =>{
        console.log(err);
      })
  };

  // this is going to delete the item from the
  const deleteFriend = () => {
    const payload = { userID: localUserID, friendID: localfriendsId };
    axios
      .post("https://letsbuckit.herokuapp.com/api/fr-remove", payload)
      .then((response) => {
        var responseList = response.data.results;
        console.log(responseList);
        window.location.reload(true);
      });
  };

  useEffect(() => {
    getFriends();
  }, []);

  const togglePopup = () => {
    setButtonPopup({
      popupState: !buttonPopup.popupState
    });
  };

  return (
    <div class="friendContainer">
                        <link rel="preconnect" href="https://fonts.gstatic.com"></link>
        <link href="https://fonts.googleapis.com/css2?family=Montserrat:wght@700&display=swap" rel="stylesheet"></link>

  <button class="btn returnHomeButton">Return Home</button>
  <button class="btn addFriendButton" onClick={ () => togglePopup}>Add friend +</button>
  {buttonPopup.popupState ? <Popup toggle={togglePopup} userID={localUserID} /> : null}
<div class="friendBoxContainer">
  <FriendContainer state={state}/>
  </div>
</div>
  );
}

export default Friends;
