import React, { useEffect, useState } from "react";
import axios from "axios";
import { API_BASE_URL, ACCESS_TOKEN_NAME } from "../constants/apiConstants.js";

import "./Email.css";
// this is to change the login username
function Name(props) {
  // local details
  const [state, setState] = useState({
    login: "",
    newLogin: "",
    password: "",
    successMessage: null,
  });
  const handleChange = (e) => {
    const { id, value } = e.target;
    setState((prevState) => ({
      ...prevState,
      [id]: value,
    }));
  };
  const jwt = require("jsonwebtoken");
  var storage = require("../tokenStorage.js");

  var tok = storage.retrieveToken();
  var ud = jwt.decode(tok, { json: true });

  var localUserID = ud.userID;

  const handleSubmitClick = (e) => {
    e.preventDefault();
    const payload = {
      //needs to be changed to email maybe.
      userID: localUserID,
      login: state.login,
      newLogin: state.newLogin,
      password: state.password,
    };
    axios
      .post("https://letsbuckit.herokuapp.com/api/change-email", payload)
      .then(function (response) {
        if (response.status === 200) {
          setState((prevState) => ({
