import React, { useEffect, useState } from "react";
import axios from "axios";
import navbar from "./Navbar.js";
import { Route, withRouter, Link, Switch } from "react-router-dom";
import { API_BASE_URL, ACCESS_TOKEN_NAME } from "../constants/apiConstants.js";

import "./Email.css";
// this is to change the login username
function Name(props) {
  // local details
  const [state, setState] = useState({
    login: "",
    newLogin: "",
    password: "",
    successMessage: null,
  });
  const handleChange = (e) => {
    const { id, value } = e.target;
    setState((prevState) => ({
      ...prevState,
      [id]: value,
    }));
  };
  const jwt = require("jsonwebtoken");
  var storage = require("../tokenStorage.js");

  var tok = storage.retrieveToken();
  var ud = jwt.decode(tok, { json: true });

  var localUserID = ud.userID;

  const handleSubmitClick = (e) => {
    e.preventDefault();
    const payload = {
      //needs to be changed to email maybe.
      userID: localUserID,
      login: state.login,
      newLogin: state.newLogin,
      password: state.password,
    };
    axios
      .post("https://letsbuckit.herokuapp.com/api/change-login", payload)
      .then(function (response) {
        if (response.status === 200) {
          setState((prevState) => ({
            ...prevState,
            successMessage: "Update successful. Redirecting to settings page..",
          }));
          localStorage.setItem(ACCESS_TOKEN_NAME, response.data.token);
          redirectToHome();
          props.showError(null);
        } else if (response.code === 204) {
          props.showError("Password Incorrect");
        }
      })
      .catch(function (error) {
        console.log(error);
      });
  };

  return (
    <div class="registerContainer">
      <link rel="preconnect" href="https://fonts.gstatic.com"></link>
      <link href="https://fonts.googleapis.com/css2?family=Montserrat:wght@700&display=swap" rel="stylesheet"></link>
      <div class="registerTitle">Setting Name</div>
      <input class="form-control formItem" id="login" onChange={handleChange} value={state.login} placeholder="Old Username"></input>
      <input class="form-control formItem" id="newLogin" onChange={handleChange} value={state.newLogin}  placeholder="New Username"></input>
      <input class="form-control formItem" id="password" onChange={handleChange} value={state.password}  placeholder="Password"></input>
      <div class="links">Back To Home</div>
      <button class="btn registerButton">Change Password</button>
    </div>
  );
}

export default Name;
