              <div className="form-inner">
                <h2>Update Email</h2>
                <div class="form-group">
                  <label htmlFor="newEmail"> Email </label>
                  <input
                    type="text"
                    name="email"
                    id="email"
                    value={state.email}
                    onChange={handleChange}
                  />
                </div>
                <div class="form-group">
                  <label htmlFor="oldEmail">New Email</label>
                  <input
                    type="text"
                    name="newEmail"
                    id="newEmail"
                    value={state.newEmail}
