import React, { setState, useState } from "react";
import "./Popup.js";
import "./Friends.css";
import { makeStyles } from "@material-ui/core/styles";
import Card from "@material-ui/core/Card";
import CardContent from "@material-ui/core/CardContent";
import Typography from "@material-ui/core/Typography";
import Grid from "@material-ui/core/Grid";
import Popup from "./Popup.js";
import axios from "axios";
const useStyles = makeStyles({
  root: {
    minWidth: 150,
    margin: "0 20px",
    backgroundColor: "#1E5F74",
    color: "#FFFF",
  },
  bullet: {
    display: "inline-block",
    margin: "0 2px",
    transform: "scale(0.8)",
  },
  title: {
    fontSize: 14,
  },
  pos: {
    marginBottom: 12,
  },
});

function Friends() {
  const [buttonPopup, setButtonPopup] = useState(false);
  const getFriends = () => {
    //get is getting every response by the endpoint
    axios
      .get("https://letsbuckit.herokuapp.com/api/fr-allfriends")
      .then((response) => {
        console.log(response);
      });
  };
  const jwt = require("jsonwebtoken");
  var storage = require("../tokenStorage.js");

  var tok = storage.retrieveToken();
  var ud = jwt.decode(tok, { json: true });

  var localUserID = ud.userID;
  var firstName = ud.firstName;
  var lastName = ud.lastName;

  const getAllFriends = async () => {
    const payload = { userID: localUserID };
    const response = await axios.post(
      "https://letsbuckit.herokuapp.com/api/fr-allfriends",
      payload
    );
    if (response.status == 500) {
      responseListItems = [];
    } else {
      var responseListItems = response.data.results;
    }
    console.log(responseListItems);
    setState((prevState) => ({
      ...prevState,
      currentListItems: responseListItems,
    }));
  };

  const classes = useStyles();
  const bull = <span className={classes.bullet}>â€¢</span>;
  return (
    <div>
      <button onClick={getAllFriends}> Get Data from API </button>

      <input
        type="text"
        class="form-control searchBar"
        placeholder="Search Friends..."
      ></input>
      <Grid container spacing={1}>
        <Grid Item spacing={3}>
          <Card className={classes.root}>
            <CardContent>
              <Typography
                className={classes.title}
                color="textSecondary"
                gutterBottom
              >
                Added Friend
              </Typography>
              <Typography variant="h5" component="h2"></Typography>
              <Typography variant="body2" component="p">
                John's dream is to see Japan
              </Typography>
            </CardContent>
          </Card>
        </Grid>
        <Grid Item spacing={3}>
          <Card className={classes.root}>
            <CardContent>
              <Typography
                className={classes.title}
                color="textSecondary"
                gutterBottom
              >
                Added Friend
              </Typography>
              <Typography variant="h5" component="h2">
                Timmy Turner
              </Typography>
              <Typography variant="body2" component="p">
                Timmy wants to visit the zoo
              </Typography>
            </CardContent>
          </Card>
        </Grid>
        <Grid Item spacing={3}>
          <Card className={classes.root}>
            <CardContent>
              <Typography
                className={classes.title}
                color="textSecondary"
                gutterBottom
              >
                Added Friend
              </Typography>
              <Typography variant="h5" component="h2">
                Jim Jones
              </Typography>
              <Typography variant="body2" component="p">
                Jim wants to visit the store
              </Typography>
            </CardContent>
          </Card>
        </Grid>
      </Grid>
      <button className="button" onClick={() => setButtonPopup(true)}>
        Add new Friend
      </button>
      <Popup trigger={buttonPopup} setTrigger={setButtonPopup}>
        <h3></h3>
      </Popup>
    </div>
  );
}

export default Friends;
