import React, { useState } from "react";
import axios from "axios";
import "./RegistrationForm.css";
import { API_BASE_URL, ACCESS_TOKEN_NAME } from "../../constants/apiConstants";
import {
  Route,
  BrowserRouter as Router,
  Link,
  Switch,
  withRouter,
} from "react-router-dom";
import sendPassword from "../SendPass";
function RegistrationForm(props) {
  const [state, setState] = useState({
    firstName: "",
    lastName: "",
    login: "",
    email: "",
    password: "",
    confirmPassword: "",
    successMessage: null,
  });
  const handleChange = (e) => {
    const { id, value } = e.target;
    setState((prevState) => ({
      ...prevState,
      [id]: value,
    }));
  };
  const sendDetailsToServer = () => {
    if (state.email.length && state.password.length) {
      props.showError(null);
      const payload = {
        firstName: state.firstName,
        lastName: state.lastName,
        login: state.login,
        email: state.email,
        password: state.password,
      };
      axios
        .post("https://letsbuckit.herokuapp.com/api/register", payload)
        .then(function (response) {
          if (response.status === 200) {
            setState((prevState) => ({
              ...prevState,
              successMessage:
                "Registration successful. Redirecting to home page..",
            }));
            localStorage.setItem(ACCESS_TOKEN_NAME, response.data.token);
            redirectToHome();
            props.showError(null);
          } else {
            props.showError("Some error ocurred");
          }
        })
        .catch(function (error) {
          console.log(error);
        });
    } else {
      props.showError("Please enter valid username and password");
    }
  };

  // this is to redirect the props to different pages
  const redirectToHome = () => {
    props.updateTitle("Home");
    props.history.push("/home");
  };
  const redirectToLogin = () => {
    props.updateTitle("Login");
    props.history.push("/login");
  };

  const redirectToRecovery = () => {
    props.history.push("/sendPassword");
  };
  const handleSubmitClick = (e) => {
    e.preventDefault();
    if (state.password === state.confirmPassword) {
      sendDetailsToServer();
    } else {
      props.showError("Passwords do not match");
    }
  };
  return (
    <Route>
      <div className="card col-12 col-lg-4 login-card mt-2 hv-center">
        <form>
          {/* This is for the First Name */}
          <div className="form-group text-left">
            <label htmlFor="firstName">First Name</label>
            <input
              type="text"
              className="form-control"
              id="firstName"
              placeholder="First Name"
              value={state.firstName}
              onChange={handleChange}
            />
          </div>

          {/* This is for the Last Name */}
          <div className="form-group text-left">
            <label htmlFor="lastName">Last Name</label>
            <input
              type="text"
              className="form-control"
              id="lastName"
              placeholder="Last Name"
              value={state.lastName}
              onChange={handleChange}
            />
          </div>
          {/* This is for the username */}
          <div className="form-group text-left">
            <label htmlFor="login">Username</label>
            <input
              type="text"
              className="form-control"
              id="login"
              placeholder="Username"
              value={state.login}
              onChange={handleChange}
            />
          </div>
          {/* This is for the Email */}
          <div className="form-group text-left">
            <label htmlFor="exampleInputEmail1">Email address</label>
            <input
              type="email"
              className="form-control"
              id="email"
              aria-describedby="emailHelp"
              placeholder="Enter email"
              value={state.email}
              onChange={handleChange}
            />
            <small id="emailHelp" className="form-text text-white">
              Check email for confirmation.
            </small>
          </div>
          {/* This is for the password */}
          <div className="form-group text-left">
            <label htmlFor="exampleInputPassword1">Password</label>
            <input
              type="password"
              className="form-control"
              id="password"
              placeholder="Password"
              value={state.password}
              onChange={handleChange}
            />
          </div>
          {/* This is for the confirm password */}
          <div className="form-group text-left">
            <label htmlFor="exampleInputPassword1">Confirm Password</label>
            <input
              type="password"
              className="form-control"
              id="confirmPassword"
              placeholder="Confirm Password"
              value={state.confirmPassword}
              onChange={handleChange}
            />
          </div>
          {/* Submit button */}
          <button
            type="submit"
            className="btn btn-warning"
            onClick={handleSubmitClick}
          >
            Register
          </button>
        </form>
        <div
          className="alert alert-success mt-2"
          style={{ display: state.successMessage ? "block" : "none" }}
          role="alert"
        >
          {state.successMessage}
        </div>
        <div className="mt-2">
          <span>Already have an account? </span>
          <span className="loginText" onClick={() => redirectToLogin()}>
            Login here
          </span>
          <div>
            Forgot password?
            <span className="loginText">
              <Link target={"_blank"} to="/sendPassword">
                <h1>Reset Password</h1>
              </Link>
              <Switch>
                <Route
                  path="/sendPassword"
                  component={sendPassword}
                  exact={true}
                />
              </Switch>
              Reset Here
            </span>
          </div>
        </div>
      </div>
    </Route>
  );
}

export default withRouter(RegistrationForm);
